package definitions;import io.cucumber.java.en.And;import io.cucumber.java.en.Given;import pages.CareersHome;import java.util.Map;import static support.TestContext.getData;public class CareersStepDefs {        @Given("I open {string} page")        public void iOpenPage(String page) {            switch (page) {                case "careers":                    new CareersHome().open();                    break;                default:                    throw new RuntimeException("Unrecognized" + page);            }        }    @And("I login as {string}")    public void iLoginAs(String role) throws Throwable {        Map<String, String> recruiter = getData(role);        new CareersHome().                clickLogin().                fillUsername(recruiter.get("email")).                fillPassword(recruiter.get("password")).                clickSubmit();    }}